import React, { useState, useEffect } from 'react';
import { BrowserMultiFormatReader, NotFoundException } from '@zxing/library';
import axios from 'axios';

function App() {
  const [scanResult, setScanResult] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const codeReader = new BrowserMultiFormatReader();

  useEffect(() => {
    if (isScanning) {
      startScanning();
    }
    return () => {
      codeReader.reset();
    };
  }, [isScanning]);

  const startScanning = async () => {
    try {
      const videoInputDevices = await codeReader.listVideoInputDevices();
      const selectedDeviceId = videoInputDevices[0].deviceId;

      const controls = await codeReader.decodeOnceFromVideoDevice(selectedDeviceId, 'video');
      setScanResult(`Scanned: ${controls.getText()}`);
      fetchDeal(controls.getText());
    } catch (err) {
      if (err instanceof NotFoundException) {
        setScanResult('No barcode found. Keep scanning...');
      } else {
        setScanResult('Error: ' + err.message);
      }
      setTimeout(startScanning, 1000); // Retry every second
    }
  };

  const fetchDeal = async (barcode) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/dewey?barcode=${barcode}`);
      setScanResult(`Deal: ${response.data[0]?.itemName || 'Not found'}`);
    } catch (err) {
      setScanResult('Error fetching deal: ' + err.message);
    }
  };

  const handleScan = () => {
    setIsScanning(true);
  };

  const stopScan = () => {
    setIsScanning(false);
    setScanResult('');
    codeReader.reset();
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h1>BuyFly ðŸ¦‰</h1>
      <p>Welcome to your deal-finding app!</p>
      {!isScanning ? (
        <button onClick={handleScan} style={{ padding: '10px 20px' }}>
          Start Scanning
        </button>
      ) : (
        <button onClick={stopScan} style={{ padding: '10px 20px' }}>
          Stop Scanning
        </button>
      )}
      {scanResult && <p style={{ marginTop: '20px' }}>{scanResult}</p>}
      <video id="video" style={{ display: isScanning ? 'block' : 'none', width: '100%', maxWidth: '500px' }} />
    </div>
  );
}

export default App;
