import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [deals, setDeals] = useState([]);

  useEffect(() => {
    // Fetch deals from the server
    axios.get('http://localhost:5000/api/dewey')
      .then(response => setDeals(response.data))
      .catch(error => console.error('Error fetching deals:', error));
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    // Filter deals client-side (server-side filtering can be added later)
    const filteredDeals = deals.filter(deal =>
      deal.name?.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setDeals(filteredDeals);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>BuyFly ðŸ¦‰</h1>
      <p>Welcome to your deal-finding app!</p>
      <input
        type="text"
        value={searchTerm}
        onChange={handleSearch}
        placeholder="Search deals..."
        style={{ padding: '5px', marginBottom: '10px', width: '200px' }}
      />
      <ul>
        {deals.length > 0 ? (
          deals.map((deal, index) => (
            <li key={index}>{deal.name} - ${deal.price || 'N/A'}</li>
          ))
        ) : (
          <li>No deals available</li>
        )}
      </ul>
    </div>
  );
}

export default App;
